const btn=document.querySelector(".switch-btn"),video=document.querySelector(".video-container");btn.addEventListener("click",()=>{btn.classList.toggle("slide"),btn.classList.contains("slide")?video.pause():video.play()});const preloader=document.querySelector(".preloader");window.addEventListener("load",()=>{window.setTimeout(()=>{preloader.classList.add("hide-preloader")},500)});